import React, {useState, useEffect} from "react";
import "../App.css";
import Axios from "axios";
//import { withCookies, Cookies } from 'react-cookie';
import Cookies from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.css';
import { Nav, Navbar, NavDropdown, NavItem, Button } from 'react-bootstrap';
import { useHistory } from 'react-router'
import Select from "../common/Dropdown";

function EditProfile(){
    const history = useHistory();
    const [loggedInUser, setLoginUser] = useState("");
    const [data, setData] = useState("");
    const [profileData, setProfileData] = useState([]);
    const [DOBReg, setDOBReg] = useState("");
    const [facilityReg, setFacilityReg] = useState("");
    const [offenseReg, setOffenseReg] = useState("");
    const [sentenceReg, setSentenceReg] = useState("");
    /* add new field from new user  */
    const [ssnReg, setSSNReg] = useState(0);
    const [photoIddReg, setPhotoIddReg] = useState(0);
    const [birthCertificateReg, setBirthCertificateReg] = useState(0);

    const SelectData = ["male", "female", "other"];
    const [genderReg, setGenderReg] = useState(response.data[0].gender);

    const [domViolenceReg, setDomViolenceReg] = useState(0);
    const [hasKidsReg, setHasKidsReg] = useState(0);
    const [medicalConditionReg, setMedicalConditionReg] = useState(0);
    const [mentalHealthReg, setMentalHealthReg] = useState(0);
    const [substanceDisorderReg, setSubstanceDisorderReg] = useState(0);

    const cookieExists = (document.cookie.match(/^(?:.*;)?\s*userId\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1]

    Axios.defaults.withCredentials = true;

    const logoutUser = () => {
        Axios.post('http://localhost:3001/logout', { } , { withCredentials: 'true' });
        history.push("/login");
        window.location.reload();
    }

    const cancelEditFunc = () => {
        history.push("/profile");
        window.location.reload();
    }

    const edit = () => {
        Axios.post("http://localhost:3001/profile/editProfile", {
            dob: DOBReg.length > 0 ? DOBReg : profileData[0].dob,
            facility_name: facilityReg.length > 0 ? facilityReg : profileData[0].facility_name,
            offense: offenseReg.length > 0 ? offenseReg : profileData[0].offense,
            sentence_length: sentenceReg.length > 0 ? sentenceReg : profileData[0].sentence_length,
            /* add new field from new user by dinhle  */
            ssn: ssnReg.length > 0 ? ssnReg : profileData[0].ssn,
            photo_id: photoIddReg.length > 0 ? photoIddReg : profileData[0].photo_id,
            birth_certificate: birthCertificateReg.length > 0 ? birthCertificateReg : profileData[0].birth_certificate,
            gender: genderReg.length > 0 ? genderReg : profileData[0].gender,
            dom_violence: domViolenceReg.length > 0 ? domViolenceReg : profileData[0].dom_violence,
            has_kids: hasKidsReg.length > 0 ? hasKidsReg : profileData[0].has_kids,
            medical_condition: medicalConditionReg.length > 0 ? medicalConditionReg : profileData[0].medical_condition,
            mental_health: mentalHealthReg.length > 0 ? mentalHealthReg : profileData[0].mental_health,
            substance_disorder: substanceDisorderReg.length > 0 ? substanceDisorderReg : profileData[0].substance_disorder

            }).then((response) => { 
                console.log("User Profile Updated: ", response);
                history.push("/profile")
     });
    }

    useEffect(()=> {
        Axios.get("http://localhost:3001/login").then((response) => {
           if (response.data.loggedIn == true){
                setLoginUser(response.data.user[0].username)
              setData(response.data)
           }
        })

        Axios.get("http://localhost:3001/profile").then((response) => {
            setProfileData(response.data)
            console.log(response)
        })

        if (loggedInUser.length > 0){
            console.log("User Logged In")
        }
        else{
            console.log("user not logged in")
        }
     }, []);
    
    return(
        <div className="editProfile">
        <Navbar bg="success" variant="dark" fixed="top" expand="lg" collapseOnSelect>
         <Navbar.Brand>Reentry and Corrections</Navbar.Brand>
         <Navbar.Toggle />
         <Navbar.Collapse>
         <Nav>
            <Nav.Link href="/home">Home</Nav.Link>
            <NavDropdown title="Organizations" id="navbarScrollingDropdown">
                <NavDropdown.Item href="/housing">Housing Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/employment">Employment Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/food">Food Organizations</NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="/favorites">Favorites</Nav.Link>
            <Nav.Link href="/profile">Profile</Nav.Link>
            <Nav.Link onClick={logoutUser}> logout </Nav.Link>
         </Nav>
         </Navbar.Collapse>
      </Navbar>
            {profileData.length >= 0 ?
            profileData.map(function(user){
                
                return (
                <div className="container Profile">
                <div className="header">{loggedInUser}'s Profile</div>
                <div className="section">
                    <div className="pt-2 pb-2">Date Of Birth</div>
                    <input type= "date" defaultValue = {user.dob} onChange={(e) => {setDOBReg(e.target.value);}}/>
                    <div className="pt-2 pb-2">Facility Name</div>
                    <input type= "text" defaultValue={user.facility_name} 
                    onChange={(e) => {
                        setFacilityReg(e.target.value);
                    }}/>
                    <div className="pt-2 pb-2">Do you have a felony offense?</div>
                    <input type= "text" defaultValue = {user.offense} 
                    onChange={(e) => {
                        setOffenseReg(e.target.value);
                    }}/>
                    <div className="pt-2 pb-2">Sentence Length</div>
                    <input type= "text" defaultValue={user.sentence_length} 
                    onChange={(e) => {
                        setSentenceReg(e.target.value);
                    }}/>


                    <div className="pt-2 pb-2">Do you have an SSN Card?</div>
                    <span> Yes </span>
                    <input  type= "radio" 
                            name="ssn_radio"
                            value={1}
                            defaultChecked={user.ssn === 1}
                            onChange={(e) => {
                                setSSNReg(e.target.value);
                            }}
                    />
                    <span> No </span>
                    <input  type= "radio" 
                            name="ssn_radio"
                            value={0}
                            defaultChecked={user.ssn === 0}
                            onChange={(e) => {
                                setSSNReg(e.target.value);
                            }}
                    />

                    <div className="pt-2 pb-2">Do you have photo ID?</div>
                    <span> Yes </span>
                    <input  type= "radio" 
                            name="photoid_radio"
                            value={1}
                            defaultChecked={user.photo_id === 1}
                            onChange={(e) => {
                                setPhotoIddReg(e.target.value);
                            }}
                    />
                    <span> No </span>
                    <input  type= "radio" 
                            name="photoid_radio"
                            value={0}
                            defaultChecked={user.photo_id === 0}
                            onChange={(e) => {
                                setPhotoIddReg(e.target.value);
                            }}
                    />


                    <div className="pt-2 pb-2">Do you have a birth certificate?</div>
                    <span> Yes </span>
                    <input  type= "radio" 
                            name="birth_radio"
                            value={1}
                            defaultChecked={user.birth_certificate === 1}
                            onChange={(e) => {
                                setBirthCertificateReg(e.target.value);
                            }}
                    />
                    <span> No </span>
                    <input  type= "radio" 
                            name="birth_radio"
                            value={0}
                            defaultChecked={user.birth_certificate === 0}
                            onChange={(e) => {
                                setBirthCertificateReg(e.target.value);
                            }}
                    />
                    <div className="pt-2 pb-2">What gender do you identify as?</div>
                    <Select
                        value={genderReg}
                        handleChange={e => setGenderReg(e.target.value)}
                        data={SelectData}
                    />

                    <div className="pt-2 pb-2">Are you a survivor of domestice violence?</div>
                    <span> Yes </span>
                    <input  type= "radio" 
                            name="dom_radio"
                            value={1}
                            defaultChecked={user.dom_violence === 1}
                            onChange={(e) => {
                                setDomViolenceReg(e.target.value);
                            }}
                    />
                    <span> No </span>
                    <input  type= "radio" 
                            name="dom_radio"
                            value={0}
                            defaultChecked={user.dom_violence === 0}
                            onChange={(e) => {
                                setDomViolenceReg(e.target.value);
                            }}
                    />

                    <div className="pt-2 pb-2">Do you have kids under the age of 18?</div>
                    <span> Yes </span>
                    <input  type= "radio" 
                            name="has_kids_radio"
                            value={1}
                            defaultChecked={user.has_kids === 1}
                            onChange={(e) => {
                                setHasKidsReg(e.target.value);
                            }}
                    />
                    <span> No </span>
                    <input  type= "radio" 
                            name="has_kids_radio"
                            value={0}
                            defaultChecked={user.has_kids === 0}
                            onChange={(e) => {
                                setHasKidsReg(e.target.value);
                            }}
                    />

                    <div className="pt-2 pb-2">Do you have a medical condition?</div>
                    <span> Yes </span>
                    <input  type= "radio" 
                            name="medical_condition_radio"
                            value={1}
                            defaultChecked={user.medical_condition === 1}
                            onChange={(e) => {
                                setMedicalConditionReg(e.target.value);
                            }}
                    />
                    <span> No </span>
                    <input  type= "radio" 
                            name="medical_condition_radio"
                            value={0}
                            defaultChecked={user.medical_condition === 0}
                            onChange={(e) => {
                                setMedicalConditionReg(e.target.value);
                            }}
                    />


                    <div className="pt-2 pb-2">Do you have a mental health diagnosis?</div>
                    <span> Yes </span>
                    <input  type= "radio" 
                            name="mental_health_radio"
                            value={1}
                            defaultChecked={user.mental_health === 1}
                            onChange={(e) => {
                                setMentalHealthReg(e.target.value);
                            }}
                    />
                    <span> No </span>
                    <input  type= "radio" 
                            name="mental_health_radio"
                            value={0}
                            defaultChecked={user.mental_health === 0}
                            onChange={(e) => {
                                setMentalHealthReg(e.target.value);
                            }}
                    />


                    <div className="pt-2 pb-2">Do you have substance use disorder?</div>
                    <span> Yes </span>
                    <input  type= "radio" 
                            name="substance_disorder_radio"
                            value={1}
                            defaultChecked={user.substance_disorder === 1}
                            onChange={(e) => {
                                setSubstanceDisorderReg(e.target.value);
                            }}
                    />
                    <span> No </span>
                    <input  type= "radio" 
                            name="substance_disorder_radio"
                            value={0}
                            defaultChecked={user.substance_disorder === 0}
                            onChange={(e) => {
                                setSubstanceDisorderReg(e.target.value);
                            }}
                    />
                </div>
                <div className="mt-3">
                <Button variant="primary" className="cancel" onClick={cancelEditFunc}>Cancel</Button>
                <Button variant="primary" className="submit" onClick={edit}>Submit</Button>
                </div>
                </div>
                )
            }) : history.push("/login")
        }
        </div>
    );
}

export default EditProfile;
