import React, { useState, useEffect } from "react";
import "../App.css";
import Axios from "axios";
//import { withCookies, Cookies } from 'react-cookie';
import Cookies from "js-cookie";
import "bootstrap/dist/css/bootstrap.css";
import { Nav, Navbar, NavDropdown, NavItem, Button } from "react-bootstrap";
import { useHistory } from "react-router";
import { useGlobalConfigContext } from "../App";

function Profile() {
  const history = useHistory();
  const [loggedInUser, setLoginUser] = useState("");
  const [data, setData] = useState("");
  const [profileData, setProfileData] = useState([]);
  const cookieExists = (document.cookie.match(
    /^(?:.*;)?\s*userId\s*=\s*([^;]+)(?:.*)?$/
  ) || [, null])[1];

  Axios.defaults.withCredentials = true;

  const logoutUser = () => {
    Axios.post("http://localhost:3001/logout", {}, { withCredentials: "true" });
    history.push("/login");
    window.location.reload();
  };

  const editProfileFunc = () => {
    history.push("/editProfile");
    window.location.reload();
  };

  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn == true) {
        setLoginUser(response.data.user[0].username);
        setData(response.data);
        Axios.get("http://localhost:3001/profile").then((response) => {
          setProfileData(response.data);
          console.log(response);
        });
      } else {
        history.push("/login");
      }
    });
  }, []);

  return (
    <div className="">
      <Navbar
        className = "navbar-custom"
        variant="dark"
        fixed="top"
        expand="lg"
        collapseOnSelect
      >
        <Navbar.Brand>Reentry and Corrections</Navbar.Brand>
        <Navbar.Toggle />
        <Navbar.Collapse>
          <Nav>
            <Nav.Link href="/home">Home</Nav.Link>
            <NavDropdown title="Organizations" id="navbarScrollingDropdown">
              <NavDropdown.Item href="housing">
                Housing Organizations
              </NavDropdown.Item>
              <NavDropdown.Item href="/employment">
                Employment Organizations
              </NavDropdown.Item>
              <NavDropdown.Item href="/basicNeeds">
                Basic Needs
              </NavDropdown.Item>
              <NavDropdown.Item href="/health">
                Health Organizations
              </NavDropdown.Item>
              <NavDropdown.Item href="/hotline">
                Hotline Services
              </NavDropdown.Item>
              <NavDropdown.Item href="/information">
                Social Service Information
              </NavDropdown.Item>
              <NavDropdown.Item href="/mentalHealth">
                Mental Health Organizations
              </NavDropdown.Item>
              <NavDropdown.Item href="/substanceUse">
                Substance Use Services
              </NavDropdown.Item>
              <NavDropdown.Item href="/universal">
                Universal Services
              </NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="/favorites">Favorites</Nav.Link>
            <Nav.Link href="/profile">Profile</Nav.Link>
            <Nav.Link onClick={logoutUser}> Logout </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      {profileData.length >= 0
        ? profileData.map(function(user) {
            return (
              <div className="container Profile">
                <div className="header">{loggedInUser}'s Profile</div>
                <div className="body">
                  <div className="section">
                    Date of Birth : {user.dob.length > 0 ? user.dob : "N/A"}
                  </div>
                  <div className="section">
                    Facility Name :{" "}
                    {user.facility_name.length > 0 ? user.facility_name : "N/A"}
                  </div>
                  <div className="section">
                    Offense : {user.offense.length > 0 ? user.offense : "N/A"}
                  </div>
                  <div className="section">
                    Sentence Length :{" "}
                    {user.sentence_length > 0
                      ? user.sentence_length + " year(s)"
                      : "N/A"}
                  </div>
                  <div className="section">
                    SSN : {user.ssn > 0 ? "Yes" : "No"}
                  </div>
                  <div className="section">
                    Photo Id : {user.photo_id > 0 ? "Yes" : "No"}
                  </div>
                  <div className="section">
                    Birth Certificate :{" "}
                    {user.birth_certificate > 0 ? "Yes" : "No"}
                  </div>
                  <div className="section">
                    Gender : {user.gender.length > 0 ? user.gender : "N/A"}
                  </div>
                  <div className="section">
                    Dom violence : {user.dom_violence > 0 ? "Yes" : "No"}
                  </div>
                  <div className="section">
                    Has kids : {user.has_kids > 0 ? "Yes" : "No"}
                  </div>
                  <div className="section">
                    Medical Condition :{" "}
                    {user.medical_condition > 0 ? "Yes" : "No"}
                  </div>
                  <div className="section">
                    Mental Health : {user.mental_health > 0 ? "Yes" : "No"}
                  </div>
                  <div className="section">
                    Substance Disorder :{" "}
                    {user.substance_disorder > 0 ? "Yes" : "No"}
                  </div>
                  <Button
                    className="edit"
                    variant="primary"
                    onClick={editProfileFunc}
                  >
                    Edit
                  </Button>
                </div>
              </div>
            );
          })
        : history.push("/login")}
    </div>
  );
}

export default Profile;
