import React, {useState, useEffect} from "react";
import "../App.css";
import Axios from "axios";
//import { withCookies, Cookies } from 'react-cookie';
import Cookies from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.css';
import { Nav, Navbar, NavDropdown, NavItem, Card, Button} from 'react-bootstrap';
import { Redirect } from "react-router";
import { useHistory } from 'react-router'
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import favorites from "../images/favorites.jpg"
import classNames from "classnames"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBookmark as fasB} from '@fortawesome/free-solid-svg-icons'
import { faBookmark as farB} from '@fortawesome/free-regular-svg-icons'
import Popup from "../common/Popup";

function Favorites(){
  const history = useHistory();
  const [loggedInUser, setLoginUser] = useState("");
  const [data, setData] = useState("")
  const [orgData, setOrgData] = useState([])
  const [unit_org, setUnitOrgData] = useState([])
  const [active, setActive] = useState(false)
  const [favData, setFavData] = useState([])

  Axios.defaults.withCredentials = true;
  const cookieExists = (document.cookie.match(/^(?:.*;)?\s*userId\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1]
    /* add by dinhle */
    const [buttonPoup, setButtonPoup] = useState(false);
    const [checked, setChecked] = React.useState(false);
    const handleChangePoupFilterByPhotoID = () => {
        setChecked(!checked);
        if(!checked) {
            Axios.get("http://localhost:3001/favorites/hasID").then((response) => {
                setOrgData(response.data)
            })
        } else {
            Axios.get("http://localhost:3001/favorites").then((response) => {
                setOrgData(response.data)
            }) 
        }
    };

    const Checkbox = ({ label, value, onChange }) => {
        return (
        <label>
            <input type="checkbox" checked={value} onChange={onChange} />
            {label}
        </label>
        );
    };
    const logoutUser = () => {
        Axios.post('http://localhost:3001/logout', { } , { withCredentials: 'true' });
        history.push("/login");
        window.location.reload();
    }

    const getOrganizations = (id) => {
      Axios.get("http://localhost:3001/favorites/" + id).then((response) => {
          setActive(id)
          setUnitOrgData(response.data[0])
      })
    }

    const checkActive = (id) => {
      return id === active
    }

    const removeFromFavorites = (obj)  => {
        Axios.delete("http://localhost:3001/favorites/", {
            data: {
                name : obj.name,
                organization_id: obj.org_id
            }
          })
          .then(function (response) {
            console.log(response);
            window.location.reload();
          })
          .catch(function (error) {
            console.log(error);
          });
    }

    useEffect(()=> {
        Axios.get("http://localhost:3001/login").then((response) => {
           if (response.data.loggedIn == true){
              setLoginUser(response.data.user[0].username)
              setData(response.data)
           }
        })

        Axios.get("http://localhost:3001/favorites/").then((response) => {
            if(response.data.length > 0) {
            setOrgData(response.data)
            getOrganizations(response.data[0].org_id)
            }
        })

     }, []);

    if (loggedInUser.length > 0){
        console.log("User Logged In")
    }
    else{
        console.log("user not logged in")
    }

    return(
        <div className="container home">
        <Navbar bg="success" variant="dark" fixed="top" expand="lg" collapseOnSelect>
         <Navbar.Brand>Reentry and Corrections</Navbar.Brand>
         <Navbar.Toggle />
         <Navbar.Collapse>
         <Nav>
            <Nav.Link href="/home">Home</Nav.Link>
            <NavDropdown title="Organizations" id="navbarScrollingDropdown">
                <NavDropdown.Item href="housing">Housing Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/employment">Employment Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/basicNeeds">Basic Needs</NavDropdown.Item>
                <NavDropdown.Item href="/health">Health Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/hotline">Hotline Services</NavDropdown.Item>
                <NavDropdown.Item href="/information">Social Service Information</NavDropdown.Item>
                <NavDropdown.Item href="/mentalHealth">Mental Health Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/substanceUse">Substance Use Services</NavDropdown.Item>
                <NavDropdown.Item href="/universal">Universal Services</NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="/favorites">Favorites</Nav.Link>
            <Nav.Link href="/profile">Profile</Nav.Link>
            <Nav.Link onClick={logoutUser}> Logout </Nav.Link>
         </Nav>
         
         </Navbar.Collapse>
      </Navbar>
      <div className="row justify-content-center">
                <div className="">
                    <Card style={{cursor: 'pointer'}} onClick="">
                        <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={favorites} />
                        <Card.Body>
                        <Card.Title>Favorites</Card.Title>
                        </Card.Body>
                    </Card>
                </div>
            </div>

            <div className="org_filter">
                <main>
                <button onClick={() => setButtonPoup(true)}>Filter Organizations</button>
                </main>

                <Popup trigger={buttonPoup} setTrigger={setButtonPoup}>
                    <span>Filter by: </span>
                    <br></br>
                    <Checkbox
                        label="Photo ID"
                        value={checked}
                        onChange={handleChangePoupFilterByPhotoID}
                    />
                </Popup>
            </div>

            {orgData.length > 0  ?
                <div className="homeDisplay">
                    <div className="disp1">
                    <h5>Total Organizations: {orgData.length}</h5>  
                {orgData.map(function(org){
                    if(loggedInUser.length == 0){
                        history.push("/login")
                    }
                    return (
                        <div onClick={() => getOrganizations(org.org_id)} className={classNames({
                            ldisp: true,
                            active: checkActive(org.org_id),
                        })}>
                            <div className="ttl">{org.name}</div>
                            <div className="subttl">{org.type}</div>
                        </div>
                    )
                })
            }
                </div>
                <div className="disp2">

                    <div className="root">
                    <span className="child">
                        <FontAwesomeIcon onClick={() => removeFromFavorites(unit_org)} icon={fasB} />
                    </span>
                    </div>

                    <div className="section">
                        <div className="secttl">Organization</div>
                        <div className="secsubttl">{unit_org.name}</div>
                    </div>
                    <div className="section">
                        <div className="secttl">Address</div>
                        <div className="secsubttl">{unit_org.address}</div>
                    </div>
                    <div className="section">
                        <div className="secttl">Zip Code</div>
                        <div className="secsubttl">{unit_org.zip_code}</div>
                    </div>
                    <div className="section">
                        <div className="secttl">Type</div>
                        <div className="secsubttl">{unit_org.type}</div>
                    </div>
                    <div className="section">
                        <div className="secttl">Description</div>
                        <div className="secsubttl">{unit_org.description}</div>
                    </div>
                    <div className="section">
                        <div className="secttl">Services</div>
                        <div className="secsubttl">{unit_org.services}</div>
                    </div>
                    <div className="section">
                        <div className="secttl">Criteria</div>
                        <div className="secsubttl">{unit_org.criteria}</div>
                    </div>
                </div>
        </div>

        : <div className="singleDisplay">
            <h3>There is no favorites</h3>
        </div>
        }       
    </div>
    );
}

export default Favorites;
