import React, { useState, useEffect } from "react";
import "../App.css";
import Axios from "axios";
//import { withCookies, Cookies } from 'react-cookie';
import Cookies from "js-cookie";
import "bootstrap/dist/css/bootstrap.css";
import {
  Nav,
  Navbar,
  NavDropdown,
  NavItem,
  Card,
  Button,
} from "react-bootstrap";
import { Redirect } from "react-router";
import { useHistory } from "react-router";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import hotline from "../images/hotline.jpg";
import classNames from "classnames";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBookmark as fasB } from "@fortawesome/free-solid-svg-icons";
import { faBookmark as farB } from "@fortawesome/free-regular-svg-icons";
import Popup from "../common/Popup";

import ReactPaginate from 'react-paginate';

function Hotline() {
  const history = useHistory();
  const [loggedInUser, setLoginUser] = useState("");
  const [data, setData] = useState("");
  const [orgData, setOrgData] = useState([]);
  const cookieExists = (document.cookie.match(
    /^(?:.*;)?\s*userId\s*=\s*([^;]+)(?:.*)?$/
  ) || [, null])[1];
  Axios.defaults.withCredentials = true;
  const [unit_org, setUnitOrgData] = useState([]);
  const [active, setActive] = useState(false);
  const [favData, setFavData] = useState([]);

  /* add by dinhle */
  const [buttonPoup, setButtonPoup] = useState(false);
  const [checked, setChecked] = React.useState(false);
  const handleChangePoupFilterByPhotoID = () => {
    setChecked(!checked);
    if (!checked) {
      Axios.get("http://localhost:3001/hotline/hasID").then((response) => {
        setOrgData(response.data);
      });
    } else {
      Axios.get("http://localhost:3001/hotline").then((response) => {
        setOrgData(response.data);
      });
    }
  };

  const Checkbox = ({ label, value, onChange }) => {
    return (
      <label>
        <input type="checkbox" checked={value} onChange={onChange} />
        {label}
      </label>
    );
  };

  const getOrganizations = (id) => {
    Axios.get("http://localhost:3001/hotline/" + id).then((response) => {
      setActive(id);
      setUnitOrgData(response.data[0]);
    });
  };

  const checkActive = (id) => {
    return id === active;
  };

  const isFavorite = (id) => {
    for (let i = 0; i < favData.length; i++) {
      if (favData[i].org_id === id) {
        return true;
      }
    }
    return false;
  };

  const getFavOrganizations = () => {
    Axios.get("http://localhost:3001/favorites/").then((response) => {
      setFavData(response.data);
    });
  };

  const addToFavorites = (obj) => {
    Axios.post("http://localhost:3001/favorites/", {
      name: obj.name,
      organization_id: obj.org_id,
    })
      .then(function(response) {
        console.log("obj.org_id " + obj.org_id);
        getInit(obj.org_id);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  const removeFromFavorites = (obj) => {
    Axios.delete("http://localhost:3001/favorites/", {
      data: {
        name: obj.name,
        organization_id: obj.org_id,
      },
    })
      .then(function(response) {
        console.log("obj.org_id " + obj.org_id);
        getInit(obj.org_id);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  const changeStatus = (obj) => {
    if (isFavorite(obj.org_id)) {
      removeFromFavorites(obj);
    } else {
      addToFavorites(obj);
    }
  };


// TODO
const [offset, setOffset] = useState(0);
const [dataOrgPaging, setDataOrgPaging] = useState([]);
const [perPage] = useState(10);
const [pageCount, setPageCount] = useState(0)

const getDataOrgPaging = async() => {
  const res = await Axios.get('http://localhost:3001/housing')
  const data = res.data;
            const slice = data.slice(offset, offset + perPage)
            const postData = slice.map(org => <div key={org.org_id}>
                <div
                  onClick={() => getOrganizations(org.org_id)}
                  className={classNames({
                    ldisp: true,
                    active: checkActive(org.org_id),
                  })}
                >
                  <div className="ttl">{org.name}</div>
                  <div className="subttl">{org.type}</div>
                </div>
            </div>)
            setDataOrgPaging(postData)
            setPageCount(Math.ceil(data.length / perPage))
}
const handlePageClick = (e) => {
  const selectedPage = e.selected;
  setOffset(selectedPage + 1)
};

useEffect(() => {
  getDataOrgPaging()
}, [offset])

  const getInit = (orgId) => {
    Axios.get("http://localhost:3001/hotline").then((response) => {
      setOrgData(response.data);
      if (orgId <= 0) {
        orgId = response.data[0].org_id;
      }
      getOrganizations(orgId);
      getFavOrganizations();
    });
  };

  const logoutUser = () => {
    Axios.post("http://localhost:3001/logout", {}, { withCredentials: "true" });
    history.push("/login");
    window.location.reload();
  };

  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn == true) {
        setLoginUser(response.data.user[0].username);
        setData(response.data);
        getInit(0);
      } else {
        history.push("/login");
      }
    });
  }, []);

  return (
    <div className="container home">
      <Navbar
        bg="success"
        variant="dark"
        fixed="top"
        expand="lg"
        collapseOnSelect
      >
        <Navbar.Brand>Reentry and Corrections</Navbar.Brand>
        <Navbar.Toggle />
        <Navbar.Collapse>
          <Nav>
            <Nav.Link href="/home">Home</Nav.Link>
            <NavDropdown title="Organizations" id="navbarScrollingDropdown">
              <NavDropdown.Item href="housing">
                Housing Organizations
              </NavDropdown.Item>
              <NavDropdown.Item href="/employment">
                Employment Organizations
              </NavDropdown.Item>
              <NavDropdown.Item href="/basicNeeds">
                Basic Needs
              </NavDropdown.Item>
              <NavDropdown.Item href="/health">
                Health Organizations
              </NavDropdown.Item>
              <NavDropdown.Item href="/hotline">
                Hotline Services
              </NavDropdown.Item>
              <NavDropdown.Item href="/information">
                Social Service Information
              </NavDropdown.Item>
              <NavDropdown.Item href="/mentalHealth">
                Mental Health Organizations
              </NavDropdown.Item>
              <NavDropdown.Item href="/substanceUse">
                Substance Use Services
              </NavDropdown.Item>
              <NavDropdown.Item href="/universal">
                Universal Services
              </NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="/favorites">Favorites</Nav.Link>
            <Nav.Link href="/profile">Profile</Nav.Link>
            <Nav.Link onClick={logoutUser}> Logout </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      <div className="row justify-content-center">
        <div className="">
          <Card style={{ cursor: "pointer" }} onClick="">
            <Card.Img
              style={{ width: "19rem", height: "10rem" }}
              variant="top"
              src={hotline}
            />
            <Card.Body>
              <Card.Title>Hotline Service</Card.Title>
            </Card.Body>
          </Card>
        </div>
      </div>

      <div className="org_filter">
        <main>
          <button onClick={() => setButtonPoup(true)}>
            Filter Organizations
          </button>
        </main>

        <Popup trigger={buttonPoup} setTrigger={setButtonPoup}>
          <span>Filter by: </span>
          <br></br>
          <Checkbox
            label="Photo ID"
            value={checked}
            onChange={handleChangePoupFilterByPhotoID}
          />
        </Popup>
      </div>



      {orgData.length > 0 ? (
        <div className="homeDisplay">
          <div className="disp1">
            <h5>Total Organizations: {orgData.length}</h5>
            {dataOrgPaging}
              <ReactPaginate
                           previousLabel={"prev"}
                           nextLabel={"next"}
                           breakLabel={"..."}
                           breakClassName={"break-me"}
                           pageCount={pageCount}
                           marginPagesDisplayed={2}
                           pageRangeDisplayed={5}
                           onPageChange={handlePageClick}
                           containerClassName={"pagination"}
                           subContainerClassName={"pages pagination"}
                           activeClassName={"active"}/>
            {/* {orgData.map(function(org) {
              if (loggedInUser.length == 0) {
                history.push("/login");
              }
              return (
                <div
                  onClick={() => getOrganizations(org.org_id)}
                  className={classNames({
                    ldisp: true,
                    active: checkActive(org.org_id),
                  })}
                >
                  <div className="ttl">{org.name}</div>
                  <div className="subttl">{org.type}</div>
                </div>
              );


            })} */}
          </div>
          <div className="disp2">
            <div className="root">
              <span className="child">
                <FontAwesomeIcon
                  onClick={() => changeStatus(unit_org)}
                  icon={isFavorite(unit_org.org_id) ? fasB : farB}
                />
              </span>
            </div>
            <div className="section">
              <div className="secttl">Organization</div>
              <div className="secsubttl">{unit_org.name}</div>
            </div>
            <div className="section">
              <div className="secttl">Address</div>
              <div className="secsubttl">{unit_org.address}</div>
            </div>
            <div className="section">
              <div className="secttl">Zip Code</div>
              <div className="secsubttl">{unit_org.zip_code}</div>
            </div>
            <div className="section">
              <div className="secttl">Type</div>
              <div className="secsubttl">{unit_org.type}</div>
            </div>
            <div className="section">
              <div className="secttl">Description</div>
              <div className="secsubttl">{unit_org.description}</div>
            </div>
            <div className="section">
              <div className="secttl">Services</div>
              <div className="secsubttl">{unit_org.services}</div>
            </div>
            <div className="section">
              <div className="secttl">Criteria</div>
              <div className="secsubttl">{unit_org.criteria}</div>
            </div>
          </div>
        </div>
      ) : (
        <h3>There is no records</h3>
      )}
    </div>
  );
}

export default Hotline;
