import React, {useState, useEffect} from "react";
import "../App.css";
import Axios from "axios";
//import { withCookies, Cookies } from 'react-cookie';
import Cookies from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.css';
import { Nav, Navbar, NavDropdown, NavItem, Button } from 'react-bootstrap';
import { useHistory } from 'react-router'

function EditProfile(){
    const history = useHistory();
    const [loggedInUser, setLoginUser] = useState("");
    const [data, setData] = useState("")
    const [profileData, setProfileData] = useState([])
    const [DOBReg, setDOBReg] = useState("")
    const [facilityReg, setFacilityReg] = useState("")
    const [offenseReg, setOffenseReg] = useState("")
    const [sentenceReg, setSentenceReg] = useState("")
    /* add new field from new user  */
    const [ssnReg, setSSNReg] = useState("")


    
    const [userDob, getUserDob] = useState("")
    const [userFacility, getUserFacility] = useState("")
    const [userOffense, getUserOffense] = useState("")
    const [userSentence, getUserSentence] = useState("")
    /* add new field from new user  */
    const [userSSN, getUserSSN] = useState("")

    const cookieExists = (document.cookie.match(/^(?:.*;)?\s*userId\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1]

    Axios.defaults.withCredentials = true;

    const logoutUser = () => {
        Axios.post('http://localhost:3001/logout', { } , { withCredentials: 'true' });
        history.push("/login");
        window.location.reload();
    }

    const cancelEditFunc = () => {
        history.push("/profile");
        window.location.reload();
    }

    const edit = () => {
        Axios.post("http://localhost:3001/profile/editProfile", {
            dob: DOBReg.length > 0 ? DOBReg : profileData[0].dob,
            facility_name: facilityReg.length > 0 ? facilityReg : profileData[0].facility_name,
            offense: offenseReg.length > 0 ? offenseReg : profileData[0].offense,
            sentence_length: sentenceReg.length > 0 ? sentenceReg : profileData[0].sentence_length

            }).then((response) => { 
                console.log("User Profile Updated: ", response);
                history.push("/profile")
     });
    }

    useEffect(()=> {
        Axios.get("http://localhost:3001/login").then((response) => {
           if (response.data.loggedIn == true){
              setLoginUser(response.data.user[0].username)
              getUserDob(response.data.user[0].dob)
              getUserFacility(response.data.user[0].facility_name)
              getUserOffense(response.data.user[0].offense)
              getUserSentence(response.data.user[0].sentence_length)

              setData(response.data)
           }
        })

        Axios.get("http://localhost:3001/profile").then((response) => {
            setProfileData(response.data)
            console.log(response)
        })

        if (loggedInUser.length > 0){
            console.log("User Logged In")
        }
        else{
            console.log("user not logged in")
        }
     }, []);

    
    
    return(
        <div className="editProfile">
        <Navbar bg="success" variant="dark" fixed="top" expand="lg" collapseOnSelect>
         <Navbar.Brand>Reentry and Corrections</Navbar.Brand>
         <Navbar.Toggle />
         <Navbar.Collapse>
         <Nav>
            <Nav.Link href="/home">Home</Nav.Link>
            <NavDropdown title="Organizations" id="navbarScrollingDropdown">
                <NavDropdown.Item href="/housing">Housing Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/employment">Employment Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/food">Food Organizations</NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="/favorites">Favorites</Nav.Link>
            <Nav.Link href="/profile">Profile</Nav.Link>
            <Nav.Link onClick={logoutUser}> logout </Nav.Link>
         </Nav>
         </Navbar.Collapse>
      </Navbar>
            {profileData.length >= 0 ?
            profileData.map(function(user){
                
                return (
                <div className="container Profile">
                <div className="header">{loggedInUser}'s Profile</div>
                <div className="section">
                    <div className="pt-2 pb-2">Date Of Birth</div>
                    <input type= "date" defaultValue = {user.dob} onChange={(e) => {setDOBReg(e.target.value);}}/>
                    <div className="pt-2 pb-2">Facility Name</div>
                    <input type= "text" defaultValue={user.facility_name} 
                    onChange={(e) => {
                        setFacilityReg(e.target.value);
                    }}/>
                    <div className="pt-2 pb-2">Offense</div>
                    <input type= "text" defaultValue = {user.offense} 
                    onChange={(e) => {
                        setOffenseReg(e.target.value);
                    }}/>
                    <div className="pt-2 pb-2">Sentence Length</div>
                    <input type= "text" defaultValue={user.sentence_length} 
                    onChange={(e) => {
                        setSentenceReg(e.target.value);
                    }}/>

                        
                    <div className="pt-2 pb-2">SSN</div>
                    <input type= "text" defaultValue={user.ssn} 
                    onChange={(e) => {
                        setSentenceReg(e.target.value);
                    }}/>
                </div>
                <div className="mt-3">
                <Button variant="primary" className="cancel" onClick={cancelEditFunc}>Cancel</Button>
                <Button variant="primary" className="submit" onClick={edit}>Submit</Button>
                </div>
                </div>
                )
            }) : history.push("/login")
        }
        </div>
    );
}

export default EditProfile;
