              </Button>
              <Button
                bsPrefix="super-btn"
                value={socialSercurityChecked}
                onClick={(e) => {
                  handleFilterHousingSocialSercurityKeyWord();
                }}
                className={classNames({
                  keyWords: true,
                  btnFilterCustom: checkHousingSocialSercurityKeyWord()
                })}
              >
                Social Security  
              </Button>
              <Button
                bsPrefix="super-btn"
