              <Button
                bsPrefix="super-btn"
                value={substanceFreeChecked}
                onClick={(e) => {
                  handleFilterHousingSubstanceFreeKeyWord();
                }}
                className={classNames({
                  keyWords: true,
                  btnFilterCustom: checkHousingSubstanceFreeKeyWord()
                })}
              >
