import React, {useState, useEffect} from "react";
import "../App.css";
import Axios from "axios";
//import { withCookies, Cookies } from 'react-cookie';
import Cookies from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.css';
import { Nav, Navbar, NavDropdown, NavItem, Card, Button} from 'react-bootstrap';
import { Redirect } from "react-router";
import { useHistory } from 'react-router'
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import housing from "../images/housing.jpg"
import employment from "../images/employment.jpg"
import basicNeeds from "../images/basicNeeds.jpg"
import favorites from "../images/favorites.jpg"
import health from "../images/health.jpg"
import hotline from "../images/hotline.jpg"
import information from "../images/information.jpg";
import mentalHealth from "../images/mentalHealth.jpg"
import substanceUse from "../images/substanceUse.jpg"
import universal from "../images/universal.jpg"
import classNames from "classnames"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBookmark as fasB} from '@fortawesome/free-solid-svg-icons'
import { faBookmark as farB} from '@fortawesome/free-regular-svg-icons'
import Popup from "../common/Popup";

function Home(){
    const history = useHistory();
    const [loggedInUser, setLoginUser] = useState("");
    const [data, setData] = useState(false)
    const [orgData, setOrgData] = useState([])
    const [favData, setFavData] = useState([])
    const [unit_org, setUnitOrgData] = useState([])
    const cookieExists = (document.cookie.match(/^(?:.*;)?\s*userId\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1]
    Axios.defaults.withCredentials = true;
    const [active, setActive] = useState(false)

    /* add by dinhle */
    const [buttonPoup, setButtonPoup] = useState(false);
    const [checked, setChecked] = React.useState(false);
    const handleChangePoupFilterByPhotoID = () => {
      setChecked(!checked);
        if(!checked) {
            Axios.get("http://localhost:3001/hasID").then((response) => {
                setOrgData(response.data)
            })
        } else {
            Axios.get("http://localhost:3001/").then((response) => {
                setOrgData(response.data)
            }) 
        }
    };

    const Checkbox = ({ label, value, onChange }) => {
        return (
        <label>
            <input type="checkbox" checked={value} onChange={onChange} />
            {label}
        </label>
        );
    };

    const logoutUser = () => {
        Axios.post('http://localhost:3001/logout', { } , { withCredentials: 'true' });
        history.push("/login");
        window.location.reload();
        
    }

    const houseFunc = () => {
        history.push("/housing");
        window.location.reload();
    }
    const employFunc = () => {
        history.push("/employment");
        window.location.reload();
    }
    const basicNeedsFunc = () => {
        history.push("/basicNeeds");
        window.location.reload();
    }
    const healthFunc = () => {
        history.push("/health");
        window.location.reload();
    }
    const hotlineFunc = () => {
        history.push("/hotline");
        window.location.reload();
    }
    const informationFunc = () => {
        history.push("/information");
        window.location.reload();
    }
    const mentalHealthFunc = () => {
        history.push("/mentalHealth");
        window.location.reload();
    }
    const substanceUseFunc = () => {
        history.push("/substanceUse");
        window.location.reload();
    }
    const universalFunc = () => {
        history.push("/universal");
        window.location.reload();
    }
    const favFunc = () => {
        history.push("/favorites");
        window.location.reload();
    }


    const isFavorite = (id) => {
        for (let i = 0; i < favData.length; i++){
            if(favData[i].org_id === id){
                return true
            }
        }
        return false
    }
    const getFavOrganizations = () => {
        Axios.get("http://localhost:3001/favorites/").then((response) => {
            setFavData(response.data)
        })
    }

    const getOrganizations = (id) => {
        Axios.get("http://localhost:3001/" + id).then((response) => {
            setActive(id)
            setUnitOrgData(response.data[0])
        })
    }
    const checkActive = (id) => {
        return id === active
    }

    const addToFavorites = (obj)  => {
        Axios.post("http://localhost:3001/favorites/", {
            name: obj.name,
            organization_id: obj.org_id
          })
          .then(function (response) {
            getInit(obj.org_id)
          })
          .catch(function (error) {
            console.log(error);
          });
    } 

    const removeFromFavorites = (obj)  => {
        Axios.delete("http://localhost:3001/favorites/", {
            data: {
                name : obj.name,
                organization_id: obj.org_id
            }
          })
          .then(function (response) {
            getInit(obj.org_id)
          })
          .catch(function (error) {
            console.log(error);
          });
    }

    const changeStatus = (obj)  => {
        if(isFavorite(obj.org_id)){
            removeFromFavorites(obj)
        } else {
            addToFavorites(obj)
        }
    }

    const getInit = (orgId)=> {
        Axios.get("http://localhost:3001/").then((response) => {
            setOrgData(response.data)
            if(orgId <= 0) {
                orgId = response.data[0].org_id;
            }
            getOrganizations(orgId)
            getFavOrganizations()
        })
    }

    useEffect(()=> {
        Axios.get("http://localhost:3001/login").then((response) => {
           if (response.data.loggedIn == true){
              setLoginUser(response.data.user[0].username)
              setData(response.data)
              getInit(0)
           }
        })

        if (loggedInUser.length > 0){
            console.log("User Logged In")
        }
        else{
            console.log("user not logged in")
        }
     }, []);

    return(
        <div className="container home">
        <Navbar bg="success" variant="dark" fixed="top" expand="lg" collapseOnSelect>
         <Navbar.Brand>Reentry and Corrections
         </Navbar.Brand>
         <Navbar.Toggle />
         <Navbar.Collapse>
         <Nav>
            <Nav.Link href="home">Home</Nav.Link>
            <NavDropdown title="Organizations" id="navbarScrollingDropdown">
                <NavDropdown.Item href="housing">Housing Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/employment">Employment Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/basicNeeds">Basic Needs</NavDropdown.Item>
                <NavDropdown.Item href="/health">Health Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/hotline">Hotline Services</NavDropdown.Item>
                <NavDropdown.Item href="/information">Social Service Information</NavDropdown.Item>
                <NavDropdown.Item href="/mentalHealth">Mental Health Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/substanceUse">Substance Use Services</NavDropdown.Item>
                <NavDropdown.Item href="/universal">Universal Services</NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="/favorites">Favorites</Nav.Link>
            <Nav.Link href="/profile">Profile</Nav.Link>
            <Nav.Link onClick={logoutUser}> Logout </Nav.Link>
         </Nav>
         </Navbar.Collapse>
      </Navbar>
        <div className="row justify-content-center">
            <div className="col-3">
                <Card style={{cursor: 'pointer'}} onClick={houseFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={housing} />
                    <Card.Body>
                    <Card.Title>Housing Organizations</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={employFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={employment} />
                    <Card.Body>
                    <Card.Title>Employment Organizations</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={basicNeedsFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={basicNeeds} />
                    <Card.Body>
                    <Card.Title>Basic Needs</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={healthFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={health} />
                    <Card.Body>
                    <Card.Title>Health Organizations</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={hotlineFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={hotline} />
                    <Card.Body>
                    <Card.Title>Hotline Services</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={informationFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={information} />
                    <Card.Body>
                    <Card.Title>Social Service Information</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={mentalHealthFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={mentalHealth} />
                    <Card.Body>
                    <Card.Title>Mental Health Organizations</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={substanceUseFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={substanceUse} />
                    <Card.Body>
                    <Card.Title>Substance Use Services</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={universalFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={universal} />
                    <Card.Body>
                    <Card.Title>Universal Services</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={favFunc}>
                    <Card.Img style={{ width: '19rem', height: '10rem'}} variant="top" src={favorites} />
                    <Card.Body>
                    <Card.Title>Favorites</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
        </div>
        <div className="homeDisplay">
            <div className="disp1">
            <h5>Total Organizations: {orgData.length}</h5>     
            {orgData.map(function(org){
                if(loggedInUser.length == 0){
                    history.push("/login")
                }
                return (
                    <div onClick={() => getOrganizations(org.org_id)} className={classNames({
                        ldisp: true,
                        active: checkActive(org.org_id),
                      })}>
                        <div className="ttl">{org.name}</div>
                        <div className="subttl">{org.type}</div>
                    </div>
                )
            })
        }
            </div>
            <div className="disp2">
                <div className="root">
                <span className="child">
                    <FontAwesomeIcon onClick={() => changeStatus(unit_org)} icon={isFavorite(unit_org.org_id) ? fasB: farB} />
                </span>
                </div>
                <div className="section">
                    <div className="secttl">Organization</div>
                    <div className="secsubttl">{unit_org.name}</div>
                </div>
                <div className="section">
                    <div className="secttl">Address</div>
                    <div className="secsubttl">{unit_org.address}</div>
                </div>
                <div className="section">
                    <div className="secttl">Zip Code</div>
                    <div className="secsubttl">{unit_org.zip_code}</div>
                </div>
                <div className="section">
                    <div className="secttl">Type</div>
                    <div className="secsubttl">{unit_org.type}</div>
                </div>
                <div className="section">
                    <div className="secttl">Description</div>
                    <div className="secsubttl">{unit_org.description}</div>
                </div>
                <div className="section">
                    <div className="secttl">Services</div>
                    <div className="secsubttl">{unit_org.services}</div>
                </div>
                <div className="section">
                    <div className="secttl">Criteria</div>
                    <div className="secsubttl">{unit_org.criteria}</div>
                </div>
            </div>
        </div>
        </div>
    );
}

export default Home;
