import React, {useState, useEffect} from "react";
import "../App.css";
import Axios from "axios";
import 'bootstrap/dist/css/bootstrap.css';
import { Nav, Navbar, Form, Button} from 'react-bootstrap';
import { useHistory } from 'react-router'
 
function ForgotPassword() {
  const history = useHistory();
  const [usernameReg, setUernameReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  const [confirmPasswordReg, setConfirmPasswordReg] = useState("");
  const [DOBReg, setDOBReg] = useState("");
  const [facilityReg, setFacilityReg] = useState("");
  const [offenseReg, setOffenseReg] = useState("");
  const [sentenceReg, setSentenceReg] = useState(0);
  const [passMatch, setPassMatch] = useState("");
  const [userThere, setUserThere] = useState("");

  const [securityQuestion1Reg, setSecurityQuestion1Reg] = useState("");
  const [securityQuestion2Reg, setSecurityQuestion2Reg] = useState("");
  const [securityQuestion3Reg, setSecurityQuestion3Reg] = useState("");
  const [securityAnswer1Reg, setSecurityAnswer1Reg] = useState("");
  const [securityAnswer2Reg, setSecurityAnswer2Reg] = useState("");
  const [securityAnswer3Reg, setSecurityAnswer3Reg] = useState("");

  const [profileData, setProfileData] = useState([]);

   //TODO1 declare a const userName, will revieve data from login, reset link
   // hardcode for now
   const [userNameReset, setUserNameReset] = useState("dinhsec"); 

  const textAlign = {
    textAlign: 'left',
    color: '#000000',
   };
   const signUp = {
    marginTop: '10px',
   };
   const signUpLink = {
    color: '#2580F6',
   };

  Axios.defaults.withCredentials = true;
  //TODO5: declare a function to check the username, with all sec questions match with ans
  // if match > show up a form to let user update new password
  // else redo

  //TODO 8: function to call to updated new passwowrd


  const resetPassword = () => {
    if (securityAnswer1Reg.length == 0){
      setUserThere("Please input all anwsers!")
      return (<h4 style={{ color: 'red' }}>Username is Required</h4>)
    }
    else if(profileData[0].answer_1 == securityAnswer1Reg && profileData[0].answer_2 == securityAnswer2Reg && profileData[0].answer_3 == securityAnswer3Reg) {
         return (<h4 style={{ color: 'red' }}>match</h4>);
    } else {
         return (<h4 style={{ color: 'red' }}> unmatch</h4>);
    }
   };

   useEffect(()=> {
       //TODO2 select all user security questions from userName recieved from Login, reset password
       console.log("userName client is: " +userNameReset );
      Axios.post("http://localhost:3001/forgotPassword/getSecQuestionbyUsername", {
         data: {
            userName : userNameReset,
         }
       }).then((response) => {
         setProfileData(response.data)
         console.log(response)
     })

     
   }, []);
   //TODO3 map all name and, sec questions to the form, readonly
   return (
     <div className="container Registration">
       <div className="signInHeader">Reset Password</div>
       <Navbar bg="success" variant="dark" fixed="top" expand="lg" collapseOnSelect>
         <Navbar.Brand>Reentry and Corrections</Navbar.Brand>
         <Navbar.Toggle />
         <Navbar.Collapse>
         <Nav>
            <Nav.Link href="/about">About Us</Nav.Link>
            <Nav.Link href="/login">Log In</Nav.Link>
         </Nav>
         </Navbar.Collapse>
      </Navbar>
      {profileData.length >= 0 ?
      profileData.map(function(user){
      return (  
      <Form>
         <Form.Group style={textAlign} className="mb-3" controlId="formBasicEmail">
            <Form.Label>Username</Form.Label>
            <Form.Control type="text" readonly   value={user.username.length > 0 ? user.username : 'N/A'}
             />
         </Form.Group>
         <Form.Group style={textAlign} className="mb-3" controlId="formBasicQuestion1">
            <Form.Label>Security Question 1</Form.Label>
            <Form.Control
            type="text" readonly value={user.security_question_1.length > 0 ? user.security_question_1 : 'N/A'}
            />
         </Form.Group>
         <Form.Group style={textAlign} className="mb-3" controlId="formBasicAnswer1">
            <Form.Label>Security Answer 1</Form.Label>
            <Form.Control 
            type="text" readonly placeholder="Security Answer 1"
            onChange={(e) => {
               setSecurityAnswer1Reg(e.target.value);
            }} 
            />
         </Form.Group>   
         <Form.Group style={textAlign} className="mb-3" controlId="formBasicQuestion2">
            <Form.Label>Security Question 2</Form.Label>
            <Form.Control 
            type="text" readonly  value={user.security_question_2.length > 0 ? user.security_question_2 : 'N/A'}
            />
         </Form.Group>
         <Form.Group style={textAlign} className="mb-3" controlId="formBasicAnswer2">
            <Form.Label>Security Answer 2</Form.Label>
         <Form.Control 
         type="text" placeholder="Security Answer 2"
         onChange={(e) => {
            setSecurityAnswer2Reg(e.target.value);
         }} 
         />
      </Form.Group>     
      <Form.Group style={textAlign} className="mb-3" controlId="formBasicQuestion3">
         <Form.Label>Security Question 3</Form.Label>
         <Form.Control 
         type="text" readonly value={user.security_question_3.length > 0 ? user.security_question_3 : 'N/A'}
         />
      </Form.Group>
      <Form.Group style={textAlign} className="mb-3" controlId="formBasicAnswer3">
         <Form.Label>Security Answer 3</Form.Label>
         <Form.Control 
         type="text" placeholder="Security Answer 3"
         onChange={(e) => {
            setSecurityAnswer3Reg(e.target.value);
         }} 
         />
      </Form.Group> 

      <Button 
      className="fullWidth"
      variant="primary"
      onClick={resetPassword}
      >
         Reset Password
      </Button>
      </Form>
      )
         }) : history.push("/login")
      }
        {userThere}<br/>
        {passMatch}
     </div>
   );
}
 
export default ForgotPassword;
