import React, {useState, useEffect} from "react";
import "../App.css";
import Axios from "axios";
//import { withCookies, Cookies } from 'react-cookie';
import Cookies from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.css';
import { Nav, Navbar, NavDropdown, NavItem, Card, Button} from 'react-bootstrap';
import { Redirect } from "react-router";
import { useHistory } from 'react-router'
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import housing from "../images/housing.jpg"
import employment from "../images/employment.jpeg"
import food from "../images/food.jpg"
import favorites from "../images/favorites.png"
import classNames from "classnames"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBookmark as fasB} from '@fortawesome/free-solid-svg-icons'
import { faBookmark as farB} from '@fortawesome/free-regular-svg-icons'

import Popup from "./popup/Popup";

function Home(){
    const history = useHistory();
    const [loggedInUser, setLoginUser] = useState("");
    const [data, setData] = useState(false)
    const [orgData, setOrgData] = useState([])
    const [favData, setFavData] = useState([])
    const [unit_org, setUnitOrgData] = useState([])
    const cookieExists = (document.cookie.match(/^(?:.*;)?\s*userId\s*=\s*([^;]+)(?:.*)?$/)||[,null])[1]
    Axios.defaults.withCredentials = true;
    const [active, setActive] = useState(false)

    /* add by dinhle */
    const [buttonPoup, setButtonPoup] = useState(false);

    const logoutUser = () => {
        Axios.post('http://localhost:3001/logout', { } , { withCredentials: 'true' });
        history.push("/login");
        window.location.reload();
        
    }

    const houseFunc = () => {
        history.push("/housing");
        window.location.reload();
    }
    const employFunc = () => {
        history.push("/employment");
        window.location.reload();
    }
    const foodFunc = () => {
        history.push("/food");
        window.location.reload();
    }
    const favFunc = () => {
        history.push("/favorites");
        window.location.reload();
    }


    const isFavorite = (id) => {
        for (let i = 0; i < favData.length; i++){
            if(favData[i].org_id === id){
                return true
            }
        }
        return false
    }
    const getFavOrganizations = () => {
        Axios.get("http://localhost:3001/favorites/").then((response) => {
            setFavData(response.data)
        })
    }

    const getOrganizations = (id) => {
        Axios.get("http://localhost:3001/" + id).then((response) => {
            setActive(id)
            setUnitOrgData(response.data[0])
        })
    }
    const checkActive = (id) => {
        return id === active
    }

    const addToFavorites = (obj)  => {
        Axios.post("http://localhost:3001/favorites/", {
            name: obj.name,
            organization_id: obj.org_id
          })
          .then(function (response) {
            getInit()
          })
          .catch(function (error) {
            console.log(error);
          });
    } 

    const removeFromFavorites = (obj)  => {
        Axios.delete("http://localhost:3001/favorites/", {
            data: {
                name : obj.name,
                organization_id: obj.org_id
            }
          })
          .then(function (response) {
            console.log(response);
            getInit()
          })
          .catch(function (error) {
            console.log(error);
          });
    }

    const changeStatus = (obj)  => {
        if(isFavorite(obj.org_id)){
            removeFromFavorites(obj)
        } else {
            addToFavorites(obj)
        }
    }

    const getInit = ()=> {
        Axios.get("http://localhost:3001/").then((response) => {
            setOrgData(response.data)
            getOrganizations(1)
            getFavOrganizations()
        })
    }

    useEffect(()=> {
        Axios.get("http://localhost:3001/login").then((response) => {
           if (response.data.loggedIn == true){
              setLoginUser(response.data.user[0].username)
              setData(true)
              getInit()
           }
        })
        if (loggedInUser.length > 0){
            console.log("User Logged In")
        }
        else{
            console.log("user not logged in")
        }

     }, []);

    return(
        <div className="container home">
        <Navbar bg="success" variant="dark" fixed="top" expand="lg" collapseOnSelect>
         <Navbar.Brand>Reentry and Corrections
         </Navbar.Brand>
         <Navbar.Toggle />
         <Navbar.Collapse>
         <Nav>
            <Nav.Link href="home">Home</Nav.Link>
            <NavDropdown title="Organizations" id="navbarScrollingDropdown">
                <NavDropdown.Item href="housing">Housing Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/employment">Employment Organizations</NavDropdown.Item>
                <NavDropdown.Item href="/food">Food Organizations</NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="/favorites">Favorites</Nav.Link>
            <Nav.Link href="/profile">Profile</Nav.Link>
            <Nav.Link onClick={logoutUser}> logout </Nav.Link>
         </Nav>
         </Navbar.Collapse>
      </Navbar>
        <div className="row justify-content-center">
            <div className="col-3">
                <Card style={{cursor: 'pointer'}} onClick={houseFunc}>
                    <Card.Img style={{ width: '18rem', height: '10rem'}} variant="top" src={housing} />
                    <Card.Body>
                    <Card.Title>Housing</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={employFunc}>
                    <Card.Img style={{ width: '18rem', height: '10rem'}} variant="top" src={employment} />
                    <Card.Body>
                    <Card.Title>Employment</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={foodFunc}>
                    <Card.Img style={{ width: '18rem', height: '10rem'}} variant="top" src={food} />
                    <Card.Body>
                    <Card.Title>Food</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
            <div className="col-3">
            <Card style={{cursor: 'pointer'}} onClick={favFunc}>
                    <Card.Img style={{ width: '18rem', height: '10rem'}} variant="top" src={favorites} />
                    <Card.Body>
                    <Card.Title>Favorites</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button className="fullWidth" variant="primary">See More</Button>
                    </Card.Body>
                </Card>
            </div>
        </div>


        <main>
            <h1>Filter</h1>
            <br></br>

        <button onClick={() => setButtonPoup(true)}>open popup</button>
        </main>

        <Popup trigger={buttonPoup} setTrigger={setButtonPoup}>
            <h3>My Popup</h3>
            <p> This is my button trigger</p>
        </Popup>


        <div className="homeDisplay">
            <div className="disp1">
            {orgData.map(function(org){
                if(loggedInUser.length == 0){
                    history.push("/login")
                }
                return (
                    <div onClick={() => getOrganizations(org.org_id)} className={classNames({
                        ldisp: true,
                        active: checkActive(org.org_id),
                      })}>
                        <div className="ttl">{org.name}</div>
                        <div className="subttl">{org.type}</div>
                    </div>
                )
            })
        }
            </div>
            <div className="disp2">
                <div className="root">
                <span className="child">
                    <FontAwesomeIcon onClick={() => changeStatus(unit_org)} icon={isFavorite(unit_org.org_id) ? fasB: farB} />
                </span>
                </div>
                <div className="section">
                    <div className="secttl">Organization</div>
                    <div className="secsubttl">{unit_org.name}</div>
                </div>
                <div className="section">
                    <div className="secttl">Address</div>
                    <div className="secsubttl">{unit_org.address}</div>
                </div>
                <div className="section">
                    <div className="secttl">Zip Code</div>
                    <div className="secsubttl">{unit_org.zip_code}</div>
                </div>
                <div className="section">
                    <div className="secttl">Type</div>
                    <div className="secsubttl">{unit_org.type}</div>
                </div>
                <div className="section">
                    <div className="secttl">Description</div>
                    <div className="secsubttl">{unit_org.description}</div>
                </div>
                <div className="section">
                    <div className="secttl">Services</div>
                    <div className="secsubttl">{unit_org.services}</div>
                </div>
                <div className="section">
                    <div className="secttl">Criteria</div>
                    <div className="secsubttl">{unit_org.criteria}</div>
                </div>
            </div>
        </div>
        </div>
    );
}

export default Home;
